import React, {useState, useMemo} from "react";
import Counter from "./components/Counter";
import DynamicInput from './components/DynamicInput';
import MyModal from "./components/UI/MyModal/MyModal";
import PostList from "./components/PostList";
import PostFilter from "./components/PostFilter";
import PostForm from "./components/PostForm";
import './styles/App.css'
import MyButton from "./components/UI/button/MyButton";

function App() {
  const [posts, setPosts] = useState ([
    {id: 1, title: 'Javascript', body: 'Javascript - Description'},
    {id: 2, title: 'C#', body: 'C# - Description'},
    {id: 3, title: 'React', body: 'React - Description'},
  ])
  const [filter, setFilter] = useState({sort: '', query: ''})
  const [modal, setModal] = useState(false);


  const sortedAndSearchedPosts = useMemo( () => {
      return sortedPosts.filter(post => post.title.toLowerCase().includes(filter.query))
  }, [filter.query, sortedPosts])

  const createPost = (newPost) => {
    setPosts([...posts, newPost]);
    setModal(false);
  }
  const removePost = (removedPost) => {
    setPosts(posts.filter(post => post.id !== removedPost.id))
  }


  return (
    <div className="App">
      <MyButton style={{marginTop: 30}} onClick={() => setModal(true)}>Создать пост</MyButton>
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost}></PostForm>
      </MyModal>
      <PostFilter filter={filter} setFilter={setFilter}/>
      <PostList remove={removePost} posts={sortedAndSearchedPosts}/>
    </div>
  );
}

export default App;
