import React, {useState} from "react";

function App() {
  const [likes, setLikes] = useState(0);
  const [inputValue, setInputValue] = useState('');

  const increment = () => {
    console.log(likes);
    setLikes(++likes);
  }
  const decrement = () => {
    console.log(likes);
    setLikes(--likes);
  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <h1>{inputValue}</h1>
      <input 
        type="text" 
        value={inputValue} 
        onchange = {event => setInputValue(event.target.inputValue)}
        style={{display:"flex"}}></input>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default App;
