import React, {useState} from "react";

function App() {
  let [likes, setLikes] = useState(0);

  const increment = () => {
    console.log(likes);
    setLikes(likes++);
  }
  const decrement = () => {
    console.log(likes);
    setLikes(likes--);
  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default App;
