import React, {useState} from "react";
import Counter from "./components/Counter";
function App() {
  const [inputValue, setInputValue] = useState('213');


  return (
    <div className="App">
      <h1>{inputValue}</h1>
      <input 
        type="text" 
        value={inputValue} 
        onChange = {event => setInputValue(event.target.value)}
        style={{display:"flex"}}></input>
        <Counter></Counter>
    </div>
  );
}

export default App;
